'use strict'

const APIBase = require('@binance/connector/src/APIBase')
const restfulModules = require('@binance/connector/src/modules/restful')
const { flowRight } = require('@binance/connector/src/helpers/utils')

class Spot extends flowRight(...Object.values(restfulModules))(APIBase) {
  constructor (apiKey = '', apiSecret = '', options = {}) {
    options.baseURL = options.baseURL || 'https://api.binance.com'
    super({
      apiKey,
      apiSecret,
      ...options
    })
  }
}

module.exports = Spot
